-- 11. Ao Nang, Krabi
UPDATE city_areas SET
  positioning_line = 'Krabi''s main tourist beach town and boat launch point',
  who_it_suits = 'First-time Krabi visitors who want a convenient base for island hopping',
  vibe_description = 'Ao Nang is a long strip of hotels, restaurants, and tour agencies facing a mediocre beach. The beach itself is not the draw. The value is in the longtail boats that leave from here to Railay, Hong Islands, and the Four Islands. It has everything you need but little charm.',
  crowd_vibe = 'Package tourists, families, and backpackers booking day trips',
  practical_info = '{"getting_around": "Walk the main strip or rent a scooter for beaches further out.", "atms": "Multiple ATMs along the beachfront road.", "wifi": "Reliable in hotels and cafes.", "budget_note": "Mid-range, tour prices are negotiable in low season."}'::jsonb
WHERE id = '823d75f3-e556-5bac-8602-d7d174dffc5b';

-- 12. Railay, Krabi
UPDATE city_areas SET
  positioning_line = 'A peninsula cut off by cliffs, reachable only by boat',
  who_it_suits = 'Climbers and beach lovers who want dramatic limestone scenery without a long boat ride',
  vibe_description = 'Railay is technically mainland Thailand but limestone cliffs block all road access. You arrive by longtail from Ao Nang in 15 minutes. West Railay has the postcard beach. East Railay is mangrove and cheaper accommodation. The climbing routes on the cliffs draw people from around the world.',
  crowd_vibe = 'Rock climbers, honeymooners, and day-trippers from Ao Nang',
  practical_info = '{"getting_around": "Walk everywhere, the peninsula is small.", "atms": "One ATM near West Railay, bring cash as backup.", "wifi": "Weak and unreliable in most places.", "budget_note": "More expensive than Ao Nang since everything arrives by boat."}'::jsonb
WHERE id = '2f579750-c504-5ea2-bbf8-4e900561377e';

-- 13. Pai Canyon Area, Pai
UPDATE city_areas SET
  positioning_line = 'The narrow ridge walk just outside town with wide valley views',
  who_it_suits = 'Active travelers who want a short hike with a big payoff at sunset',
  vibe_description = 'Pai Canyon is a narrow sandstone ridge about 8 kilometers from town. The walk along the top is short but exposed with no railings. Sunset is the best time. The area around it is quiet farmland with not much else.',
  crowd_vibe = 'Tourists on scooters making the sunset pilgrimage from town',
  practical_info = '{"getting_around": "Scooter required, no public transport out here.", "budget_note": "Free entry, just factor in scooter rental at 200-300 baht per day."}'::jsonb
WHERE id = '1dcaa3a2-fe13-5f93-9cab-c0bf8bd45ac6';

