  crowd_vibe = 'Vietnamese families, temple visitors, and tourists on organized day tours',
  practical_info = '{"getting_around": "Motorbike or taxi from Da Lat center, about 20 minutes.", "budget_note": "Cable car to Truc Lam Pagoda costs around 100,000 VND round trip."}'::jsonb
