-- 26. Cam Thanh, Hoi An
UPDATE city_areas SET
  positioning_line = 'The coconut palm village where you paddle basket boats through waterways',
  who_it_suits = 'Nature-curious travelers who want a morning away from Hoi An''s tourist center',
  vibe_description = 'Cam Thanh is a water coconut village about 4 kilometers from Hoi An''s old town. The main activity is riding round basket boats through narrow palm-lined channels. Local guides spin the boats and cast fishing nets. It''s touristy but genuinely fun for an hour or two.',
  crowd_vibe = 'Tour groups in the morning, quieter in the afternoon when the boats slow down',
  practical_info = '{"getting_around": "Bicycle from Hoi An takes 20 minutes on flat roads.", "budget_note": "Basket boat tours cost 150,000-200,000 VND per person."}'::jsonb
