INSERT INTO geo_content (id, scope, country_id, city_id, title, subtitle, summary, content_md, hero_image_url, safety_rating, solo_friendly, best_months, currency, language, visa_note, highlights, updated_by, published_at, created_at, updated_at) VALUES
-- Countries
(uuid_generate_v5(uuid_ns_url(), 'geo-country-th'), 'country', uuid_generate_v5(uuid_ns_url(), 'country-th'), NULL, 'Thailand', 'The gateway to Southeast Asia', 'Thailand is the most popular starting point for solo women travelers in Asia — and for good reason. Affordable, welcoming, and endlessly varied, it offers everything from buzzing cities to island paradise. The infrastructure is excellent and other solo travelers are everywhere.', NULL, 'https://images.unsplash.com/photo-1528181304800-259b08848526?w=800', 'generally_safe', true, 'Nov–Feb (cool season)', 'Thai Baht (THB)', 'Thai (English widely spoken in tourist areas)', 'Most nationalities get 30-60 days visa-free', ARRAY['Incredible street food scene','Easy to meet other travelers','Affordable massages everywhere','Beautiful islands in the south','Digital nomad infrastructure in Chiang Mai'], NULL, '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z'),
(uuid_generate_v5(uuid_ns_url(), 'geo-country-vn'), 'country', uuid_generate_v5(uuid_ns_url(), 'country-vn'), NULL, 'Vietnam', 'A country that rewards the curious', 'Vietnam is sensory overload in the best way — the food alone is worth the trip. Solo travel here is very doable, though the pace can be intense in the cities. The north-to-south route is a classic for a reason.', NULL, 'https://images.unsplash.com/photo-1557750255-c76072a7aad1?w=800', 'generally_safe', true, 'Oct–Apr (varies by region)', 'Vietnamese Dong (VND)', 'Vietnamese (limited English outside tourist hubs)', 'E-visa available for most nationalities (30 days)', ARRAY['World-class street food','Stunning landscapes from Sapa to Mekong','Very affordable','Rich history and culture','Growing cafe culture'], NULL, '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z'),
(uuid_generate_v5(uuid_ns_url(), 'geo-country-id'), 'country', uuid_generate_v5(uuid_ns_url(), 'country-id'), NULL, 'Indonesia', 'Islands, temples, and endless warmth', 'Bali gets all the attention, but Indonesia is a vast archipelago with something for every kind of traveler. The wellness and digital nomad scenes are thriving, and the Balinese hospitality is legendary.', NULL, 'https://images.unsplash.com/photo-1537996194471-e657df975ab4?w=800', 'generally_safe', true, 'Apr–Oct (dry season)', 'Indonesian Rupiah (IDR)', 'Bahasa Indonesia (English common in Bali)', 'Visa on arrival for most nationalities (30 days, extendable)', ARRAY['World-class yoga and wellness','Thriving coworking community in Bali','Ancient temples like Borobudur','Incredible diving and snorkeling','Affordable luxury stays'], NULL, '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z'),
(uuid_generate_v5(uuid_ns_url(), 'geo-country-ph'), 'country', uuid_generate_v5(uuid_ns_url(), 'country-ph'), NULL, 'Philippines', 'Island hopping at its finest', 'The Philippines is pure paradise for beach lovers and adventure seekers. English is widely spoken, making it one of the easiest countries in Southeast Asia for solo travelers. The locals are incredibly warm and welcoming.', NULL, 'https://images.unsplash.com/photo-1518509562904-e7ef99cdcc86?w=800', 'generally_safe', true, 'Dec–May (dry season)', 'Philippine Peso (PHP)', 'Filipino & English (widely spoken)', 'Most nationalities get 30 days visa-free', ARRAY['7,000+ islands to explore','English widely spoken','Incredible island hopping','Warm and helpful locals','Growing surf culture in Siargao'], NULL, '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z'),
(uuid_generate_v5(uuid_ns_url(), 'geo-country-jp'), 'country', uuid_generate_v5(uuid_ns_url(), 'country-jp'), NULL, 'Japan', 'Effortlessly safe, endlessly fascinating', 'Japan is arguably the safest country in the world for solo women travelers. The trains run on time, the food is extraordinary, and you can walk alone at 3am without a second thought. It is more expensive than Southeast Asia but worth every yen.', NULL, 'https://images.unsplash.com/photo-1493976040374-85c8e12f0c0e?w=800', 'very_safe', true, 'Mar–May (cherry blossom), Oct–Nov (autumn leaves)', 'Japanese Yen (JPY)', 'Japanese (English limited but improving)', 'Most Western nationalities get 90 days visa-free', ARRAY['Extremely safe at all hours','Impeccable public transport','Solo dining culture (counter seats everywhere)','Ancient temples and modern cities','Onsen (hot spring) culture'], NULL, '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z'),
(uuid_generate_v5(uuid_ns_url(), 'geo-country-pt'), 'country', uuid_generate_v5(uuid_ns_url(), 'country-pt'), NULL, 'Portugal', 'Europe''s best-kept solo travel secret', 'Portugal offers the charm of Southern Europe without the intensity of Spain or Italy. Lisbon and Porto are walkable, safe, and full of character. The food and wine are exceptional and surprisingly affordable for Western Europe.', NULL, 'https://images.unsplash.com/photo-1555881400-74d7acaacd8b?w=800', 'very_safe', true, 'Apr–Oct', 'Euro (EUR)', 'Portuguese (English widely spoken)', 'Schengen zone — 90 days for most nationalities', ARRAY['Very safe for women','Incredible pastries and wine','Strong digital nomad visa program','Beautiful coastal walks','Rich maritime history'], NULL, '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z'),
(uuid_generate_v5(uuid_ns_url(), 'geo-country-ma'), 'country', uuid_generate_v5(uuid_ns_url(), 'country-ma'), NULL, 'Morocco', 'Vibrant, intense, and deeply rewarding', 'Morocco is a sensory feast that requires some street smarts. Solo women will get attention — that is the reality. But with preparation and confidence, it is an extraordinary destination. Stick to well-reviewed riads, use guides for medinas, and trust your instincts.', NULL, 'https://images.unsplash.com/photo-1489749798305-4fea3ae63d43?w=800', 'use_caution', true, 'Mar–May, Sep–Nov', 'Moroccan Dirham (MAD)', 'Arabic, French, Berber (some English)', 'Most nationalities get 90 days visa-free', ARRAY['Stunning architecture and riads','Incredible food scene','Sahara desert trips','Blue city of Chefchaouen','Traditional hammam experiences'], NULL, '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z'),
-- Cities
(uuid_generate_v5(uuid_ns_url(), 'geo-city-bkk'), 'city', uuid_generate_v5(uuid_ns_url(), 'country-th'), uuid_generate_v5(uuid_ns_url(), 'city-bkk'), 'Bangkok', 'Chaotic, delicious, and never boring', 'Bangkok is overwhelming in the best way. The street food is legendary, the temples are stunning, and the nightlife ranges from rooftop cocktails to underground jazz. Excellent public transit makes getting around easy and safe.', NULL, 'https://images.unsplash.com/photo-1508009603885-50cf7c579365?w=800', 'generally_safe', true, 'Nov–Feb', 'THB', 'Thai', NULL, ARRAY['World-class street food','Easy BTS/MRT system','Affordable Thai massage','Vibrant nightlife options','Great backpacker infrastructure'], NULL, '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z'),
(uuid_generate_v5(uuid_ns_url(), 'geo-city-cnx'), 'city', uuid_generate_v5(uuid_ns_url(), 'country-th'), uuid_generate_v5(uuid_ns_url(), 'city-cnx'), 'Chiang Mai', 'The digital nomad capital of the world', 'Chiang Mai is where travelers come for a week and stay for a month. The Old City is walkable, the cafe scene is unreal, and the digital nomad community is the most established in Southeast Asia. Slower and cooler than Bangkok.', NULL, 'https://images.unsplash.com/photo-1598935898639-81586f7d2129?w=800', 'generally_safe', true, 'Nov–Feb (avoid Mar–Apr smoke season)', 'THB', 'Thai', NULL, ARRAY['Amazing coworking spaces','Best khao soi in the world','Walkable Old City','Temple-hopping','Sunday walking street market'], NULL, '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z'),
(uuid_generate_v5(uuid_ns_url(), 'geo-city-ubud'), 'city', uuid_generate_v5(uuid_ns_url(), 'country-id'), uuid_generate_v5(uuid_ns_url(), 'city-ubud'), 'Ubud', 'Bali''s spiritual and cultural heart', 'Ubud is the wellness capital of Southeast Asia. Yoga retreats, rice terraces, and incredible food draw solo women travelers from around the world. It can feel touristy on the main strip, but venture slightly off-path and you find magic.', NULL, 'https://images.unsplash.com/photo-1537996194471-e657df975ab4?w=800', 'generally_safe', true, 'Apr–Oct', 'IDR', 'Bahasa Indonesia', NULL, ARRAY['World-class yoga studios','Rice terrace walks','Vibrant art scene','Excellent vegetarian food','Monkey Forest sanctuary'], NULL, '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z'),
(uuid_generate_v5(uuid_ns_url(), 'geo-city-canggu'), 'city', uuid_generate_v5(uuid_ns_url(), 'country-id'), uuid_generate_v5(uuid_ns_url(), 'city-canggu'), 'Canggu', 'Surf, brunch, and sunsets', 'Canggu is Bali''s trendiest area — a magnet for digital nomads, surfers, and creatives. The cafe scene is absurd (in a good way), the beach clubs are fun, and the coworking options are plentiful. Rent a scooter or pedal a beach cruiser.', NULL, 'https://images.unsplash.com/photo-1558005137-d9619a5c539f?w=800', 'generally_safe', true, 'Apr–Oct', 'IDR', 'Bahasa Indonesia', NULL, ARRAY['Epic surf breaks for all levels','Smoothie bowl capital','Beach club sunsets','Coworking with pools','Strong solo female traveler community'], NULL, '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z'),
(uuid_generate_v5(uuid_ns_url(), 'geo-city-hoi'), 'city', uuid_generate_v5(uuid_ns_url(), 'country-vn'), uuid_generate_v5(uuid_ns_url(), 'city-hoi'), 'Hoi An', 'Lanterns, tailors, and the best banh mi', 'Hoi An is Vietnam at its most charming. The Ancient Town is a UNESCO site lit by thousands of lanterns. Cycling is the best way to get around, the tailoring is world-famous, and the food scene punches way above its weight.', NULL, 'https://images.unsplash.com/photo-1559592413-7cec4d0cae2b?w=800', 'generally_safe', true, 'Feb–May', 'VND', 'Vietnamese', NULL, ARRAY['UNESCO Ancient Town','Custom tailoring in 24 hours','Bicycle-friendly','An Bang Beach','Cooking classes'], NULL, '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z'),
(uuid_generate_v5(uuid_ns_url(), 'geo-city-elnido'), 'city', uuid_generate_v5(uuid_ns_url(), 'country-ph'), uuid_generate_v5(uuid_ns_url(), 'city-elnido'), 'El Nido', 'Paradise found', 'El Nido is postcard-perfect — towering limestone cliffs, turquoise lagoons, and white sand beaches. Island hopping tours are the main attraction, and the hostel scene makes it easy to find travel buddies for boat trips.', NULL, 'https://images.unsplash.com/photo-1518509562904-e7ef99cdcc86?w=800', 'generally_safe', true, 'Dec–May', 'PHP', 'Filipino, English', NULL, ARRAY['Island hopping tours A–D','Big and Small Lagoon','Nacpan Beach','Friendly hostel scene','Incredible snorkeling'], NULL, '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z'),
(uuid_generate_v5(uuid_ns_url(), 'geo-city-lisbon'), 'city', uuid_generate_v5(uuid_ns_url(), 'country-pt'), uuid_generate_v5(uuid_ns_url(), 'city-lisbon'), 'Lisbon', 'Hills, tiles, and pastel de nata', 'Lisbon is one of the safest and most walkable capitals in Europe. The food scene is booming, the hostel culture is famously social, and the fado music will move you. A fantastic first solo trip destination.', NULL, 'https://images.unsplash.com/photo-1555881400-74d7acaacd8b?w=800', 'very_safe', true, 'Apr–Oct', 'EUR', 'Portuguese', NULL, ARRAY['Very safe for solo women','Amazing hostel social scene','Tram 28 through Alfama','Pasteis de Belem','Rooftop bars everywhere'], NULL, '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z'),
(uuid_generate_v5(uuid_ns_url(), 'geo-city-marrakech'), 'city', uuid_generate_v5(uuid_ns_url(), 'country-ma'), uuid_generate_v5(uuid_ns_url(), 'city-marrakech'), 'Marrakech', 'Overwhelming, beautiful, and unforgettable', 'Marrakech demands confidence. The medina is a maze, the souks are intense, and solo women will attract attention. But a good riad, a reliable guide, and an open mind will reward you with one of the most memorable cities on earth.', NULL, 'https://images.unsplash.com/photo-1489749798305-4fea3ae63d43?w=800', 'use_caution', true, 'Mar–May, Sep–Nov', 'MAD', 'Arabic, French', NULL, ARRAY['Jemaa el-Fna at sunset','Jardin Majorelle','Riad stays in the Medina','Hammam experiences','Souk shopping'], NULL, '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z', '2026-01-01T00:00:00Z');

